function sizeSections(){for(var n=document.getElementsByClassName("section"),e=0;e<n.length;e++){n[e].style.height=.75*window.innerHeight+"px"}}window.onload=function(){sizeSections()},window.onresize=function(){sizeSections()},window.onscroll=function(){50<window.scrollY?document.getElementsByTagName("nav")[0].classList.add("small-nav"):document.getElementsByTagName("nav")[0].classList.remove("small-nav")};